import { Router, Request, Response } from 'express';
import fs from 'fs-extra';
import xmlbuilder from 'xmlbuilder';
import logger from '../../logger';

const router = Router();

router.post('/', (request: Request, response: Response) => {
    const action = request.headers.soapaction?.split('/').pop();
    const requestBody = request.body as any;
    const body = requestBody['SOAP-ENV:Envelope']['SOAP-ENV:Body'];

    console.log(body);
    console.log('[WUP - ECS]', action);

    response.set('Content-Type', 'text/xml');

    let actionResponse: any;
    let data: any;

    switch (action) {
        case 'GetAccountStatus':
            data = body['ecs:GetAccountStatus'];

            actionResponse = {
                'soapenv:Envelope': {
                    '@xmlns:soapenv': 'http://schemas.xmlsoap.org/soap/envelope/',
                    '@xmlns:xsd': 'http://www.w3.org/2001/XMLSchema',
                    '@xmlns:xsi': 'http://www.w3.org/2001/XMLSchema-instance',

                    'soapenv:Body': {
                        GetAccountStatusResponse: {
                            '@xmlns': 'urn:ecs.wsapi.broadon.com',
                            Version: '2.0',
                            DeviceId: data['ecs:DeviceId'],
                            MessageId: `EC-${data['ecs:DeviceId']}-3038380129`,
                            TimeStamp: Date.now(),
                            ErrorCode: '0',
                            ServiceStandbyMode: 'false',
                            AccountId: data['ecs:AccountId'],
                            AccountStatus: 'R',
                            Balance: {
                                Amount: '999',
                                Currency: 'USD',
                            },
                            EulaVersion: '0',
                            Country: 'US',
                            Region: 'USA',
                            AccountAttributes: {
                                Name: 'LOYALTY_LOGIN_NAME',
                                Value: '',
                            },
                            TIV: [],
                            ServiceURLs: [
                                {
                                    Name: 'ContentPrefixURL',
                                    URI: 'http://ccs.cdn.wup.shop.nintendo.net/ccs/download',
                                },
                                {
                                    Name: 'UncachedContentPrefixURL',
                                    URI: 'https://ccs.wup.shop.nintendo.net/ccs/download',
                                },
                                {
                                    Name: 'SystemContentPrefixURL',
                                    URI: 'http://nus.cdn.wup.shop.nintendo.net/ccs/download',
                                },
                                {
                                    Name: 'SystemUncachedContentPrefixURL',
                                    URI: 'https://ccs.wup.shop.nintendo.net/ccs/download',
                                },
                                {
                                    Name: 'EcsURL',
                                    URI: 'https://ecs.wup.shop.nintendo.net/ecs/services/ECommerceSOAP',
                                },
                                {
                                    Name: 'IasURL',
                                    URI: 'https://ias.wup.shop.nintendo.net/ias/services/IdentityAuthenticationSOAP',
                                },
                                {
                                    Name: 'CasURL',
                                    URI: 'https://cas.wup.shop.nintendo.net/cas/services/CatalogingSOAP',
                                },
                                {
                                    Name: 'NusURL',
                                    URI: 'https://nus.wup.shop.nintendo.net/nus/services/NetUpdateSOAP',
                                },
                            ],
                            IVSSyncFlag: 'false',
                            CountryAttribits: 12,
                        },
                    },
                },
            };
            break;

        case 'AccountGetETickets':
            data = body['ecs:AccountGetETickets'];
            const ticketIds = data['ecs:TicketId'];
            const tickets: string[] = [];

            for (const ticketId of ticketIds) {
                let ticket: string | undefined;

                switch (ticketId) {
                    case '1633213454495676':
                        ticket = fs.readFileSync(`${__dirname}/../../../titles/wup/title/0005000010102172/title.tik`).toString('base64');
                        break;
                    case '1504125632659492':
                        ticket = fs.readFileSync(`${__dirname}/../../../titles/wup/title/00050000101999ff/title.tik`).toString('base64');
                        break;
                    default:
                        break;
                }

                if (ticket) {
                    tickets.push(ticket);
                }
            }

            actionResponse = {
                'soapenv:Envelope': {
                    '@xmlns:soapenv': 'http://schemas.xmlsoap.org/soap/envelope/',
                    '@xmlns:xsd': 'http://www.w3.org/2001/XMLSchema',
                    '@xmlns:xsi': 'http://www.w3.org/2001/XMLSchema-instance',

                    'soapenv:Body': {
                        AccountGetETicketsResponse: {
                            '@xmlns': 'urn:ecs.wsapi.broadon.com',
                            Version: '2.0',
                            DeviceId: data['ecs:DeviceId'],
                            MessageId: `EC-${data['ecs:DeviceId']}-3038380129`,
                            TimeStamp: Date.now(),
                            ErrorCode: '0',
                            ServiceStandbyMode: 'false',
                            ETickets: tickets,
                            Certs: [
								'',

								''
                            ],
                        },
                    },
                },
            };
            break;

        default:
            actionResponse = {
                'soapenv:Envelope': {
                    '@xmlns:soapenv': 'http://schemas.xmlsoap.org/soap/envelope/',
                    '@xmlns:xsd': 'http://www.w3.org/2001/XMLSchema',
                    '@xmlns:xsi': 'http://www.w3.org/2001/XMLSchema-instance',

                    'soapenv:Body': {
                        Fault: {
                            '@xmlns': 'http://schemas.xmlsoap.org/soap/envelope/',
                            faultcode: 'soapenv:Client',
                            faultstring: 'Invalid SOAPAction',
                        },
                    },
                },
            };
    }

    const xmlResponse = xmlbuilder.create(actionResponse).end({ pretty: true });
    response.send(xmlResponse);
});

export default router;